import React from "react";
import * as Yup from "yup";
import { Form, Button } from "react-bootstrap";
import { useFormik } from "formik";
const validationSchema = Yup.object().shape({
  instituteName: Yup.string()
    .min(2, "Too Short!")
    .required("Required"),
  degree: Yup.string()
    .ensure()
    .required("Degree is Required"),
  speacialization: Yup.string()
    .required("Speacialization is Required")
    .min(1, "Too Short!"),
  other: Yup.string()
    .required("Please mention")
    .min(2, "Too Short"),
  graduationStart: Yup.date().max(new Date(), "Invalid Date"),
  graduationEnd: Yup.date().max(new Date(), "Invalid Date"),
  percentage: Yup.number().positive("Invalid Percentage")
});

const Education = () => {
  const { handleSubmit, handleChange, errors, values, handleBlur } = useFormik({
    initialValues: {
      instituteName: "",
      degree: "B.Tech",
      other: "",
      speacialization: "",
      graduationStart: "",
      graduationEnd: "",
      percentage: ""
    },
    validationSchema,
    onSubmit: values => {
      console.log(values);
    }
  });
  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label htmlFor="instituteName">Institute Name</Form.Label>
        <Form.Control
          id="instituteName"
          type="text"
          placeholder="Enter your institute name"
          name="instituteName"
          onChange={handleChange}
          value={values.instituteName}
          onBlur={handleBlur}
          isInvalid={!!errors.instituteName}
        />
        <Form.Control.Feedback type="invalid">
          {errors.instituteName && touched.instituteName}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group>
        <Form.Label htmlFor="degree">Degree</Form.Label>
        <Form.Control
          id="degree"
          as="select"
          value={values.degree}
          isInvalid={!!errors.degree}
          onBlur={handleBlur}
          onChange={handleChange}
        >
          <option value="B.Tech">B.Tech/B.S</option>
          <option value="M.Tech">M.Tech/M.S</option>
          <option value="PhD">PhD</option>
          <option value="highSchool">High School</option>
          <option value="other">Other</option>
        </Form.Control>
        {console.log(values.degree)}
        <Form.Control.Feedback type="invalid">
          {errors.degree && touched.degree}
        </Form.Control.Feedback>
      </Form.Group>
      {values.degree === "other" ? (
        <Form.Group>
          <Form.Label htmlFor="other">Please mention your degree</Form.Label>
          <Form.Control
            id="other"
            type="text"
            placeholder="Mention your degree"
            name="other"
            onBlur={handleBlur}
            onChange={handleChange}
            value={values.other}
            isInvalid={!!errors.other}
          />
          <Form.Control.Feedback type="invalid">
            {errors.other && touched.other}
          </Form.Control.Feedback>
        </Form.Group>
      ) : null}
      <Form.Group>
        <Form.Label htmlFor="speacialization">Speacialization</Form.Label>
        <Form.Control
          id="speacialization"
          onBlur={handleBlur}
          type="text"
          placeholder="Enter your Specialization"
          name="speacialization"
          onChange={handleChange}
          value={values.speacialization}
          isInvalid={!!errors.speacialization}
        />
        <Form.Control.Feedback type="invalid">
          {errors.speacialization && touched.speacialization}
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group>
        <Form.Label htmlFor="graduationStart">Start Year</Form.Label>
        <Form.Control
          id="graduationStart"
          placeholder="Enter Graduation Start Year"
          onBlur={handleBlur}
          name="graduationStart"
          type="date"
          onChange={handleChange}
          value={values.graduationStart}
          isInvalid={!!errors.graduationStart}
        />
        <Form.Control.Feedback type="invalid">
          {errors.graduationStart && touched.graduationStart}
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group>
        <Form.Label htmlFor="graduationEnd">End Year</Form.Label>
        <Form.Control
          id="graduationEnd"
          placeholder="Enter Graduation End Year"
          onBlur={handleBlur}
          name="graduationEnd"
          type="date"
          onChange={handleChange}
          value={values.graduationEnd}
          isInvalid={!!errors.graduationEnd}
        />
        <Form.Control.Feedback type="invalid">
          {errors.graduationEnd && touched.graduationEnd}
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group>
        <Form.Label htmlFor="percentage">Percentage</Form.Label>
        <Form.Control
          id="percentage"
          type="number"
          placeholder="Enter Percentage"
          onBlur={handleBlur}
          name="percentage"
          onChange={handleChange}
          value={values.percentage}
          isInvalid={!!errors.percentage}
        />
        <Form.Control.Feedback type="invalid">
          {errors.percentage && touched.percentage}
        </Form.Control.Feedback>
      </Form.Group>
      <Button type="submit">Next</Button>
    </Form>
  );
};

export default Education;
