import React from "react";
import { useFormik } from "formik";
import { Form, Button, Col, Container } from "react-bootstrap";
import * as Yup from "yup";
import styles from "./PersonalComponent.module.css";
const validationSchema = Yup.object().shape({
  fullName: Yup.string()
    .min(2, "Too Short!")
    .max(50, "Too Long!")
    .required("Required"),
  aboutMe: Yup.string()
    .min(10, "Too Short!")
    .max(1000, "Too Long!")
    .required("Required"),
  email: Yup.string()
    .email("Invalid email")
    .required("Required"),
    phoneNum: Yup.number()
    .min(12, "Invalid Number")
    .positive("Enter a valid Number")
    .required("Required"),
    country: Yup.string()
    .required("Required"),
    state: Yup.string()
    .required("Required"),
    city: Yup.string()
    .required("Required"),
    githubUrl: Yup.string()
    .url("Enter a valid Url")
    .required("Required"),
    linkedinUrl: Yup.string()
    .url("Enter a valid Url")
    .required("Required")
});

const Personal = () => {
  const {  handleChange, values, errors,handleSubmit } = useFormik({
    initialValues: {
      fullName: "",
      aboutMe: "",
      email: "",
      phoneNum: "",
      country: "",
      state: "",
      city: "",
      githubUrl: "",
      linkedinUrl: ""
    },
    validationSchema,
    onSubmit: values => {
      console.log(values);
      console.log(validationSchema);
    //   alert(JSON.stringify(values, null, 2));
    }
  });
  return (
    <Container>
      <h3 className={styles.heading}>Personal Details</h3>
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label htmlFor="fullName">Name</Form.Label>
        <Form.Control
          placeholder="Enter Your Name"
          id="fullName"
          name="fullName"
          type="text"
          onChange={handleChange}
          value={values.fullName}
          isInvalid={!!errors.fullName}
        />
        <Form.Control.Feedback type="invalid">
          {errors.fullName} 
        </Form.Control.Feedback>


      </Form.Group>

      <Form.Group>
        <Form.Label htmlFor="aboutMe">About Me</Form.Label>
        <Form.Control
          placeholder="Tell me about yourself."
          id="aboutMe"
          name="aboutMe"
          type="textarea"
          onChange={handleChange}
          value={values.aboutMe}
        />

        <Form.Text className="text-muted">Maximum 1000 words.</Form.Text>
        {errors.aboutMe ? errors.aboutMe : null}
      </Form.Group>
      <Form.Group>
        <Form.Label htmlFor="email">Email</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email address"
          id="email"
          name="email"
          onChange={handleChange}
          value={values.email}
        />
        {errors.email ? errors.email : null}
      </Form.Group>
      <Form.Group>
        <Form.Label htmlFor="phoneNum">Contact Number</Form.Label>
        <Form.Control
          type="number"
          placeholder="Enter your phone number"
          id="phoneNum"
          name="phoneNum"
          onChange={handleChange}
          value={values.phoneNum}
        />
        {errors.phoneNum ? errors.phoneNum : null}
      </Form.Group>
      <Form.Row>
      <Form.Group as={Col} md="4">
        <Form.Label htmlFor="country">Country</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your country"
          id="country"
          name="country"
          onChange={handleChange}
          value={values.country}
        />
        {errors.country ? errors.country : null}
      </Form.Group>
      <Form.Group as={Col} md="4">
        <Form.Label htmlFor="state">State</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your state"
          id="state"
          name="state"
          onChange={handleChange}
          value={values.state}
        />
        {errors.state ? errors.state : null}
      </Form.Group>
      <Form.Group as={Col} md="4">
        <Form.Label htmlFor="city">City</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your city"
          id="city"
          name="city"
          onChange={handleChange}
          value={values.city}
        />
        {errors.city ? errors.city : null}
      </Form.Group>
      </Form.Row>
      <Form.Row>
      <Form.Group as={Col} md="6">
        <Form.Label htmlFor="githubUrl">Github URL</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your githubUrl"
          id="githubUrl"
          name="githubUrl"
          onChange={handleChange}
          value={values.githubUrl}
        />
        {errors.githubUrl ? errors.githubUrl : null}
      </Form.Group>
      <Form.Group as={Col} md="6">
        <Form.Label htmlFor="linkedinUrl">LinkedIn URL</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your linkedInUrl"
          id="linkedinUrl"
          name="linkedinUrl"
          onChange={handleChange}
          value={values.linkedinUrl}
        />
        {errors.linkedinUrl ? errors.linkedinUrl : null}
      </Form.Group>
      </Form.Row>
      <Button type="submit">Next</Button>
    </Form>
    </Container>
  );
};

export default Personal;
